mapper.sql:
where 1=1

	and task_user.id = #{id}


	and user_code = #{userCode}


	and task_code = #{taskCode}


	and status = #{status}


	and task_scoring = #{taskScoring}


	and grading_teacher = #{gradingTeacher}


	and grad_time = #{gradTime}

mapper.update:
update mis.task_user set
id = id

	,status = #{status}


	,task_scoring = #{taskScoring}
	,grad_time = now()


	,info = #{info}


	,grading_teacher = #{gradingTeacher}


,task_student = #{taskStudent}

where id = #{id}

update mis.task_user set
id = id

	,status = #{status}


	,task_scoring = #{taskScoring}
	,grad_time = now()


	,info = #{info}


	,grading_teacher = #{gradingTeacher}

where user_code = #{userCode}
and task_code = #{taskCode}

mapper.delete:
delete from
mis.task_user
where task_code = #{taskCode} and user_code = #{userCode}

mapper.select:
select
,task_user.id ,task_teacher ,task_user.info, task_student ,user_management.user_name ,task_info.task_name from mis.task_user left join mis.user_management on task_user.user_code = user_management.user_code left join mis.task_info on task_user.task_code = task_info.task_code
and mis.user_management.user_code = #{userCode}
and mis.user_management.parent_code = #{parentCode}
and mis.user_management.class_name = #{className}
limit ${rowStart},${pageLimit}
